import React, { Component } from "react";
import API from "../utils/API";
import { Col, Row, Container } from "../components/Grid";
import { Input, TextArea, FormBtn } from "../components/Form";
import MusicArea from "../components/MusicArea";

class Main extends Component {
  state = {
    books: [],
    title: "",
    author: "",
    lyrics: "",
    music: ""
  };

  componentDidMount() {
    this.loadBooks();
  }

  loadBooks = () => {
    API.getBooks()
      .then(res =>
        this.setState({ books: res.data, title: "", author: "", synopsis: "" })
      )
      .catch(err => console.log(err));
  };

  deleteBook = id => {
    API.deleteBook(id)
      .then(res => this.loadBooks())
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    if (this.state.title && this.state.author) {
      API.saveBook({
        title: this.state.title,
        author: this.state.author,
        lyrics: this.state.lyrics,
        music: this.state.music
      })
        .then(res => this.loadBooks())
        .catch(err => console.log(err));
    }
  };

  save = project => {
    alert("project saved");
  };

  border = {
    border: "solid 1px black",
    height: "500px"
  };

  render() {
    return (
      <Container fluid>
        <h1>Main Page Stuff</h1>
        <Row>
          <Col size="md-8 sm-12">
            <div style={this.border}>
              <h2>Lyrics go here</h2>
              <form>
                <TextArea onChange={this.handleInputChange} />
                <FormBtn onClick={this.save}>Save</FormBtn>
              </form>
            </div>
          </Col>
          <Col size="md-4 sm-12">
            <div style={this.border}>
            <h2>Music goes here</h2><hr />
              <MusicArea />
            </div>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Main;
